            ll v=e[i].to;
            dr[v]--;
            if(dr[v]==0)  q.push(v),vis[v]=1;
        }
    }
    ll ans=INF;
    for(ll i=1;i<=n;i++){
        if(!vis[i]){
            ans=min(ans,dfs(i));
