```cpp
/*
* @Author: Marhoosh
* @Date:   2021-10-11 11:00:11
* @Last Modified by:   Marhoosh
* @Last Modified time: 2021-10-30 15:52:27
*/
#include<bits/stdc++.h>
using namespace std;
#define Debug(x) cout<<#x<<':'<<x<<endl
#define INF 0x7fffffff
typedef long long ll;
typedef pair<ll,ll> P;
const ll maxn=2e3+11;
vector<P> ans;
ll fa[maxn],d[maxn];
ll n,m;
ll found(ll x){return fa[x]==x?x:fa[x]=found(fa[x]);}
void solve1(){
    ll e=0;
    for(ll i=1;i<=n;i++){
        while(d[i]<2){
            for(ll j=n+1;j<=2*n;j++){
                if(d[j]<2){
                    ll fi=found(i),fj=found(j);
                    if(fi==fj)  continue;
                    d[i]++,d[j]++;
                    ans.push_back({i,j});
                    //cout<<i<<" "<<j<<endl;
                    fa[fi]=fj;
                    e++;
                    if(e>=2*n-m-1)  return ;
                    break;
                }
            }
        }
    }
}
void solve2(){
    ll u,v;
    for(ll i=1;i<=2*n;i++){
        if(d[i]==1){u=i;break;}
    }
    for(ll i=1;i<=2*n;i++){
        if(d[i]==1 && i!=u){v=i;break;}
    }
    ans.push_back({u,v});
    cout<<ans.size()<<endl;
    for(auto i=ans.begin();i!=ans.end();i++){
        cout<<(*i).first<<" "<<(*i).second-n<<endl;
    }
    return ;
}
int main(){
    ios::sync_with_stdio(false);
    cin.tie(0);
    ll ca;cin>>ca;
    while(ca--){
        cin>>n>>m;
        for(ll i=1;i<=2*n;i++)  fa[i]=i;
        ans.clear();memset(d,0,sizeof(d));
        for(ll i=1;i<=m;i++){
            ll u,v;cin>>u>>v;
            v=v+n;
            d[u]++,d[v]++;
            fa[u]=v;
        }
        solve1();
        solve2();
    }
    return 0;
}
