#include<bits/stdc++.h>
using namespace std;
#define Debug(x) cout<<#x<<':'<<x<<endl
#define INF 0x7fffffff
typedef long long ll;
typedef long double ld;
typedef pair<ll,ll> P;
const ll maxn=2e5+11;
const ld pi=acos(-1);
ll n,m;
void solve(){
    ld ans=0;
    if(m==1){
        ans=1.0/6.0*(2*n+2)*(2*n+1)*(2*n)-n*(n+1);//此处推公式可以用到裂项相消法
        printf("%.10Lf\n",ans);
        return ;
    }
    for(ld r2=1;r2<=n;r2++){
        //圆内:
        ld ans1=0;
        for(ld i=1;i<=m-1;i++){
            if(i*pi*r2/m<2*r2){
                ans1=ans1+r2*(r2-1.0)/2.0*(i*pi/m+1);
            }
            else{
                ans1=ans1+3*r2/2.0*(r2-1);// /2出错
            }
        }  
        ans1=ans1*2;
        ans1=ans1+r2*(2*r2-1);
        ans1=ans1*2*m;
        ans+=ans1;
        //圆上：
        ld ans2=0;
        for(ld i=1;i<=m-1;i++){
            ans2+=min(i*pi*r2/m,(ld)2*r2);
        }
        ans2=ans2*2+2*r2;
        ans=ans+ans2*2*m/2.0;
    }
    printf("%.10Lf\n",ans);
}
int main(){
    cin>>n>>m;
    solve();
    return 0;
}
