*/
#include<bits/stdc++.h>
using namespace std;
#define Debug(x) cout<<#x<<':'<<x<<endl
#define INF 0x7fffffff
typedef long long ll;
typedef pair<ll,ll> P;
const ll maxn=1e2+11;
struct Node{
    ll a[maxn][maxn];
}city[maxn];
ll gra[maxn][maxn];
ll n,m;
void matirx(ll u,ll t){
    ll mid[maxn][maxn];
    for(ll i=1;i<=m;i++){
        for(ll j=1;j<=m;j++){
            mid[i][j]=0;
            for(ll k=1;k<=m;k++){
                mid[i][j]+=city[u].a[i][k]*city[t].a[k][j];
            }
        }
    }
    for(ll v=1;v<=n;v++){
        if(v==u || v==t)  continue;
        ll flag=1;
        for(ll i=1;i<=m;i++){
            if(flag==0)  break;
            for(ll j=1;j<=m;j++){
                if(mid[i][j]!=city[v].a[i][j]){
                    flag=0;break;
                }
            }
        }
        if(flag)  gra[u][v]=1;
    }
}
void floyd(){
    for(ll k=1;k<=n;k++){
        for(ll i=1;i<=n;i++){
            for(ll j=1;j<=n;j++)  gra[i][j]=min(gra[i][j],gra[i][k]+gra[k][j]);
        }
    }
}
int main(){
    ios::sync_with_stdio(false);
    cin.tie(0);
    while(cin>>n>>m && (n||m)){
        memset(gra,0x1f,sizeof(gra));
        for(ll c=1;c<=n;c++){
            for(ll i=1;i<=m;i++){
                for(ll j=1;j<=m;j++)  cin>>city[c].a[i][j];
            }
        }
        for(ll i=1;i<=n;i++){
            for(ll j=1;j<=n;j++){
                if(i==j)  continue;
                matirx(i,j);
            }
        }
        floyd();
        ll q;cin>>q;
        for(ll i=1;i<=q;i++){
            ll u,v;cin>>u>>v;
            if(gra[u][v]>1e9)  cout<<"Sorry"<<endl;
            else  cout<<gra[u][v]<<endl;
        }
    }
    return 0;
}
