#include<bits/stdc++.h>

using namespace std;

int N,MAX,A[10],T;

int ABS(int x){
	if(x<0)return -x;
	return x;
}

void dfs(int ans,int d){
	if(d==N){
		if(ABS(ans)<MAX)MAX=ABS(ans);
		return;
	}
	dfs(ans+A[d],d+1);
	dfs(ans-A[d],d+1);
}

int main(){
	scanf("%d",&T);
	while(T--){
		scanf("%d",&N);
		MAX=1E8;
		for(int i=0;i<N;i++){
			scanf("%d",&A[i]);
		}
		dfs(0,0);
		printf("%d\n",MAX);
	}
}
