struct Edge{
    ll to,next;
}edge[maxn];
ll head[maxn],c[maxn],d[maxn],n,k,tmp=1;
queue<ll> q;
void add(ll u,ll v){
    edge[tmp].to=v;edge[tmp].next=head[u];
    head[u]=tmp++;
}
int main(){
    ios::sync_with_stdio(false);
    cin.tie(0);
    cin>>n>>k;
    for(ll i=1,u,v;i<=n-1;i++){
        cin>>u>>v;
        add(u,v);add(v,u);c[u]++;c[v]++;
    }
    for(ll i=1;i<=n;i++)  if(c[i]==1)  {
        q.push(i);d[i]=1;
    }
    ll cnt=n-k;
    while(1){
        ll x=q.front();q.pop();
        if(--cnt==0){
            cout<<d[x]<<endl;
            return 0;
        }
        for(ll i=head[x];i;i=edge[i].next){
            ll v=edge[i].to;c[v]--;
            if(c[v]==1){
                q.push(v);d[v]=d[x]+1;
            }
        }
    }
    return 0;
}
