        }
        else if(in[v])  low[u]=min(low[u],dfn[v]);
    }
    if(low[u]==dfn[u]){
        num++;ll v;
        do{
            v=st[--top];in[v]=0;
            c[v]=num;w[num]=min(w[num],val[v]);
        }while(u!=v);
    }
}
void solve(){
    for(ll i=1;i<=n;i++){
        if(!dfn[i])  tarjan(i);
    }
    for(ll i=1;i<tmp;i++){
        ll u=fr[i],v=to[i];
        if(c[u]==c[v])  continue;
        dr[c[v]]++;
    }
    ll ans1=0,ans2=0;
    for(ll i=1;i<=num;i++){
        if(dr[i]==0){
            ans1++;
            ans2+=w[i];
        }
    }
    cout<<ans1<<" "<<ans2<<endl;
}
void init(){
    memset(head,0,sizeof(head));memset(dfn,0,sizeof(dfn));
    memset(c,0,sizeof(c));memset(dr,0,sizeof(dr));
    memset(w,0x3f,sizeof(w));
    tmp=cnt=1;num=0;
}
int main(){
    ios::sync_with_stdio(false);
    cin.tie(0);
    while(cin>>n>>m){
        init();
        for(ll i=1;i<=n;i++)  cin>>val[i];
        for(ll i=1;i<=m;i++){
            ll u,v;cin>>u>>v;add(u,v);
        }
        solve();
    }
    return 0;
}

