const ll maxn=1e3+11;
const ll maxm=2e6+11;
ll to[maxm],nex[maxm],head[maxn],dfn[maxn],low[maxn],d[maxn];
ll n,m,cnt,num,tmp;
bool bridge[maxm],t[maxm];
void add(ll u,ll v){
    to[tmp]=v;nex[tmp]=head[u];head[u]=tmp++;
