using namespace std;
#define Debug(x) cout<<#x<<':'<<x<<endl
#define INF 0x7fffffff
typedef long long ll;
typedef pair<ll,ll> P;
const ll maxn=2e5+11;
struct Edge{
    ll fr,to,w,type;
    bool operator < (const Edge& b) const {
        return w<b.w;
    }
}e[maxn];
ll f1[maxn],f2[maxn];
ll n1,m1,n2,m2;
ll found1(ll x){
    return f1[x]==x?x:f1[x]=found1(f1[x]);
}
ll found2(ll x){
    return f2[x]==x?x:f2[x]=found2(f2[x]);
}
int main(){
    ios::sync_with_stdio(false);
    cin.tie(0);
    cin>>n1>>m1>>n2>>m2;
    for(ll i=0;i<n1;i++)  f1[i]=i;
    for(ll i=0;i<n2;i++)  f2[i]=i;
    ll tmp=0;
    for(ll i=1;i<=m1;i++){
        ll u,v,w;cin>>u>>v>>w;
        e[tmp++]={u,v,w,1};
    }
    for(ll i=1;i<=m2;i++){
        ll u,v,w;cin>>u>>v>>w;
        e[tmp++]={u,v,w,2};
    }
    sort(e,e+tmp);
    ll cnt=0,cnt1=0,cnt2=0,p=0,ans=0;
    while(cnt<=n1+n2-2 && p<tmp){
        ll ty=e[p].type;
        if(ty==1){
            if(cnt1>=n1-1){
                p++;continue;
            }
            ll u=e[p].fr,v=e[p].to;
            ll fu=found1(u),fv=found1(v);
            if(fu==fv){
                p++;continue;
            }  
            f1[fu]=fv;
            ans=ans+(n2-cnt2)*e[p].w;
            cnt1++;cnt++;p++;
        }
        else{
            if(cnt2>=n2-1){
                p++;continue;
            }
            ll u=e[p].fr,v=e[p].to;
            ll fu=found2(u),fv=found2(v);
            if(fu==fv){
                p++;continue;
            } 
            f2[fu]=fv;
            ans=ans+(n1-cnt1)*e[p].w;
            cnt2++;cnt++;p++;
        }
    }
    cout<<ans<<endl;
    return 0;
}
