struct Edge{
    ll fr,to,t,next;
}edge[maxn];
ll head[maxn],fr[maxn],to[maxn],t[maxn],vis[maxn],disf[maxn],diss[maxn],tmp,root;
struct Point{
    ll l,p;
    bool operator < (const Point& b) const{
        return l>b.l;
    }
};
