    init();memset(diss,0x3f,sizeof(diss));
    for(ll i=1;i<=m;i++){
        add(to[i],fr[i],t[i]);
    }
    dijkstra2();
    ll ans=0;
    for(ll i=1;i<=n;i++){
        if(i==root)  continue;
        ans=max(ans,disf[i]+diss[i]);
