#include<iostream>
#include<cstring>
#include<queue>
#include<algorithm>
#pragma comment(linker, "/STACK:102400000,102400000")
using namespace std;
#define Debug(x) cout<<#x<<':'<<x<<endl
#define INF 0x7fffffff
typedef long long ll;
const ll maxn=1e5+11;
struct Edge{
    ll to,w,t,next;
}edge[maxn];
ll ar[maxn],head[maxn],dis[maxn],vis[maxn],u[maxn],v[maxn],w[maxn],t[maxn];
ll n,m,total,tmp,start,goal,step;
struct Point{
    ll w,pos;
    bool operator < (const Point& b) const{
        return w>b.w;
    }
};
priority_queue<Point> dq;
void add(ll u,ll v,ll w,ll t){
    edge[tmp]={v,w,t};edge[tmp].next=head[u];
    head[u]=tmp++;
}
void dijkstra(){
    dis[start]=0;
    dq.push({0,start});
    while(!dq.empty()){
        ll u=dq.top().pos;dq.pop();
        if(vis[u]==1)  continue;
        vis[u]=1;
        for(ll i=head[u];i;i=edge[i].next){
            ll v=edge[i].to;
            if(!vis[v] && dis[u]+edge[i].t<dis[v]){
                dis[v]=dis[u]+edge[i].t;
                dq.push({dis[v],v});
            }
        }
    }
}
void init(){
    memset(head,0,sizeof(head));memset(dis,0x1f,sizeof(dis));
    memset(vis,0,sizeof(vis));
}
ll check(ll mid){
    tmp=1;init();
    for(ll i=1;i<=m;i++){
        if(w[i]>=ar[mid]){
            add(u[i],v[i],w[i],t[i]);add(v[i],u[i],w[i],t[i]);
        }
    }
    dijkstra();
    if(dis[goal]<=total)  return 1;
    else  return 0;
}
void solve(){
    ll l=0,r=step;
    while(l<r){
        ll mid=(l+r)>>1;
        if(check(mid))  r=mid;
        else  l=mid+1;
    }
    cout<<ar[l]<<endl;
}
int main(){
    ios::sync_with_stdio(false);
    cin.tie(0);
    ll ca;cin>>ca;
    while(ca--){
        cin>>n>>m>>total;
        start=1;goal=n;step=0;
        for(ll i=1;i<=m;i++){
            cin>>u[i]>>v[i]>>w[i]>>t[i];
            ar[i-1]=w[i];
        }
        step=m;
        sort(ar,ar+step,greater<ll>());
        solve();
    }
    return 0;
}

