}
void push_down(ll l,ll r,ll now){
    ll m=l+((r-l)>>1);
    if(lazy[now]){
        t[now<<1]+=lazy[now]*(m-l+1),lazy[now<<1]+=lazy[now];
        t[now<<1|1]+=lazy[now]*(r-m),lazy[now<<1|1]+=lazy[now];
        lazy[now]=0;
    }
}
void build(ll l,ll r,ll now){
    if(l==r){
        t[now]=ar[l];
        return ;
