    ll t=found(fa[x]);
    d[x]=d[x]+d[fa[x]];
    return fa[x]=t;
}
int main(){
    ll ca;scanf("%lld",&ca);
    while(ca--){
        tmp=1;
        scanf("%lld",&n);
        for(ll i=1;i<=n;i++){
            head[i]=0;fa[i]=i;d[i]=0;
        }
        for(ll i=1;i<=n-1;i++){
            ll u,v;scanf("%lld%lld",&u,&v);
            add(u,v);add(v,u);
        }
        for(ll i=1;i<=n;i++){
            scanf("%lld",&var[i]);
            po[i]={var[i],i};
        }
        sort(po+1,po+1+n);
        for(ll i=1;i<=n;i++){
            ll u=po[i].second,w=po[i].first;
            for(ll j=head[u];j;j=nex[j]){
                ll v=to[j];
                if(var[v]>w)  continue;
                ll fu=found(u),fv=found(v);
                fa[fv]=fu;d[fv]=1;
            }
        }
        for(ll i=1;i<=n;i++){
            found(i);
            printf("%lld\n",d[i]+1);
        }
    }
    return 0;
}

```

